pragma solidity 0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "../Bdx/BDXShares.sol";
import "../Oracle/IOracleBasedCryptoFiatFeed.sol";
import "../Oracle/ICryptoPairOracle.sol";
import "./Pools/BdStablePool.sol";
import "./Pools/BdPoolLibrary.sol";

contract BDStable is ERC20Upgradeable, OwnableUpgradeable {
    using SafeERC20 for IERC20;

    /* ========== STATE VARIABLES ========== */
    enum PriceChoice {
        BDSTABLE,
        BDX
    }

    uint8 private constant MAX_NUMBER_OF_POOLS = 32;
    uint256 public unclaimedPoolsBDX;

    IERC20 private BDX;
    address public treasury;

    ICryptoPairOracle private bdstableWethOracle;
    ICryptoPairOracle private bdxWethOracle;

    IOracleBasedCryptoFiatFeed private weth_fiat_pricer;

    uint256 public global_collateral_ratio_d12; // 12 decimals of precision

    address public weth_address;

    // The addresses in this array are added by the oracle and these contracts are able to mint bdStable
    address payable[] public bdstable_pools_array;

    // Mapping is also used for faster verification
    mapping(address => bool) public bdstable_pools;

    uint256 public bdStable_step_d12; // Amount to change the collateralization ratio by upon refreshCollateralRatio()
    uint256 public refresh_cooldown; // Seconds to wait before being able to run refreshCollateralRatio() again
    uint256 public price_target_d12; // The price of BDSTABLE at which the collateral ratio will respond to; this value is only used for the collateral ratio mechanism and not for minting and redeeming which are hardcoded at 1 <fiat>
    uint256 public price_band_d12; // The bound above and below the price target at which the refreshCollateralRatio() will not change the collateral ratio

    uint256 private minimumMintRedeemDelayInBlocks = 1;

    bool public collateral_ratio_paused;

    mapping(address => uint256) public lastMintByUserBlock;

    // There needs to be a time interval that this can be called. Otherwise it can be called multiple times per expansion.
    uint256 public refreshCollateralRatio_last_call_time; // Last time the collateral ration was refreshed function was executed

    /* ========== MODIFIERS ========== */

    modifier onlyPools() {
        require(bdstable_pools[msg.sender] == true, "Only bd pools can call this function");
        _;
    }

    modifier onlyOwnerOrPool() {
        require(msg.sender == owner() || bdstable_pools[msg.sender] == true, "You are not the owner or a pool");
        _;
    }

    /* ========== CONSTRUCTOR ========== */

    function initialize(
        string memory _name,
        string memory _symbol,
        address _treasury,
        address _bdx_address,
        uint256 _initalBdStableToTreasury_d18
    ) external initializer {
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(bytes(_symbol).length > 0, "Symbol cannot be empty");
        require(_bdx_address != address(0), "BDX address cannot be 0");

        __ERC20_init(_name, _symbol);
        __Ownable_init();

        treasury = _treasury;

        BDX = IERC20(_bdx_address);

        bdStable_step_d12 = (uint256(BdPoolLibrary.PRICE_PRECISION) * 25) / 10000; // 12 decimals of precision, equal to 0.25%
        global_collateral_ratio_d12 = uint256(BdPoolLibrary.COLLATERAL_RATIO_MAX); // Bdstable system starts off fully collateralized (12 decimals of precision)
        price_target_d12 = uint256(BdPoolLibrary.PRICE_PRECISION); // Collateral ratio will adjust according to the 1 <fiat> price target at genesis
        price_band_d12 = (uint256(BdPoolLibrary.PRICE_PRECISION) * 50) / 10000; // Collateral ratio will not adjust if between 0.995<fiat> and 1.005<fiat> at genesis
        refresh_cooldown = 3600; // Refresh cooldown period is set to 1 hour (3600 seconds) at genesis

        if (_initalBdStableToTreasury_d18 > 0) {
            _mint(_treasury, _initalBdStableToTreasury_d18); // so treasury can provide liqidity to swaps and we could get prices from the swaps
        }
    }

    /* ========== VIEWS ========== */

    function getBdStablesPoolsLength() external view returns (uint256) {
        return bdstable_pools_array.length;
    }

    // collateral value in fiat corresponding to the stable
    // Iterate through all bd pools and calculate all value of collateral in all pools globally
    function globalCollateralValue() public view returns (uint256) {
        uint256 total_collateral_value_d18 = 0;

        // bdstable_pools_array.length is limited by addPool function
        for (uint256 i = 0; i < bdstable_pools_array.length; i++) {
            total_collateral_value_d18 = total_collateral_value_d18 + (BdStablePool(bdstable_pools_array[i]).collatFiatBalance());
        }
        return total_collateral_value_d18;
    }

    // Choice = 'BDSTABLE' or 'BDX' for now
    function oracle_price(PriceChoice choice) internal view returns (uint256) {
        uint256 weth_fiat_price_d12 = weth_fiat_price();
        uint256 price_vs_weth;

        if (choice == PriceChoice.BDSTABLE) {
            price_vs_weth = uint256(bdstableWethOracle.consult(weth_address, BdPoolLibrary.PRICE_PRECISION)); // How much BDSTABLE if you put in BdPoolLibrary.PRICE_PRECISION WETH
        } else if (choice == PriceChoice.BDX) {
            price_vs_weth = uint256(bdxWethOracle.consult(weth_address, BdPoolLibrary.PRICE_PRECISION)); // How much BDX if you put in BdPoolLibrary.PRICE_PRECISION WETH
        } else revert("INVALID PRICE CHOICE. Needs to be either 0 (BDSTABLE) or 1 (BDX)");

        return (weth_fiat_price_d12 * BdPoolLibrary.PRICE_PRECISION) / price_vs_weth;
    }

    function updateOraclesIfNeeded() external {
        if (bdxWethOracle.shouldUpdateOracle()) {
            bdxWethOracle.updateOracle();
        }

        if (bdstableWethOracle.shouldUpdateOracle()) {
            bdstableWethOracle.updateOracle();
        }
    }

    function shouldUpdateOracles() external view returns (bool) {
        return bdxWethOracle.shouldUpdateOracle() || bdstableWethOracle.shouldUpdateOracle();
    }

    // Returns BDSTABLE / <fiat>
    function bdstable_price_d12() public view returns (uint256) {
        return oracle_price(PriceChoice.BDSTABLE);
    }

    // Returns BDX / <fiat>
    function BDX_price_d12() external view returns (uint256) {
        return oracle_price(PriceChoice.BDX);
    }

    function effective_global_collateral_ratio_d12() public view returns (uint256) {
        uint256 bdStable_total_supply = totalSupply();
        uint256 global_collat_value = globalCollateralValue();
        uint256 efCR = (global_collat_value * BdPoolLibrary.PRICE_PRECISION) / bdStable_total_supply;
        return efCR;
    }

    function weth_fiat_price() public view returns (uint256) {
        return uint256(weth_fiat_pricer.getPrice_1e12());
    }

    function canLegallyRedeem(address who) external view returns (bool) {
        return (block.number - lastMintByUserBlock[who]) >= minimumMintRedeemDelayInBlocks;
    }

    // Returns the value of excess collateral held in all BdStablePool related to this BdStable, compared to what is needed to maintain the global collateral ratio
    function availableExcessCollatDV() external view returns (uint256) {
        uint256 total_supply = totalSupply();
        uint256 global_collat_value = globalCollateralValue();

        // Calculates collateral needed to back each 1 BdStable with $1 of collateral at current collat ratio
        uint256 required_collat_fiat_value_d18 = (total_supply * global_collateral_ratio_d12) / BdPoolLibrary.COLLATERAL_RATIO_MAX;

        if (global_collat_value > required_collat_fiat_value_d18) {
            return (global_collat_value - required_collat_fiat_value_d18);
        } else {
            return 0;
        }
    }

    /* ========== PUBLIC FUNCTIONS ========== */

    function when_should_refresh_collateral_ratio_in_seconds() public view returns (uint256) {
        uint256 secondsSinceLastRefresh = block.timestamp - refreshCollateralRatio_last_call_time;

        return secondsSinceLastRefresh > refresh_cooldown ? 0 : (refresh_cooldown - secondsSinceLastRefresh);
    }

    function refreshCollateralRatio() external {
        if (collateral_ratio_paused == true) {
            return;
        }

        if (when_should_refresh_collateral_ratio_in_seconds() > 0) {
            return;
        }

        if (bdstableWethOracle.shouldUpdateOracle()) {
            bdstableWethOracle.updateOracle();
        }

        uint256 bdstable_price_cur = bdstable_price_d12();

        // Step increments are 0.25% (upon genesis, changable)

        if (bdstable_price_cur > (price_target_d12 + price_band_d12)) {
            //decrease collateral ratio
            if (global_collateral_ratio_d12 <= bdStable_step_d12) {
                //if within a step of 0, go to 0
                global_collateral_ratio_d12 = 0;
            } else {
                global_collateral_ratio_d12 = global_collateral_ratio_d12 - bdStable_step_d12;
            }
        } else if (bdstable_price_cur < (price_target_d12 - price_band_d12)) {
            //increase collateral ratio
            if ((global_collateral_ratio_d12 + bdStable_step_d12) >= BdPoolLibrary.COLLATERAL_RATIO_MAX) {
                global_collateral_ratio_d12 = BdPoolLibrary.COLLATERAL_RATIO_MAX; // cap collateral ratio at 1.000000
            } else {
                global_collateral_ratio_d12 = global_collateral_ratio_d12 + bdStable_step_d12;
            }
        }

        refreshCollateralRatio_last_call_time = block.timestamp; // Set the time of the last expansion

        emit CollateralRatioRefreshed(global_collateral_ratio_d12);
    }

    function get_effective_bdx_coverage_ratio() external view returns (uint256) {
        uint256 effective_collateral_ratio_d12 = effective_global_collateral_ratio_d12();

        uint256 cr = global_collateral_ratio_d12 > effective_collateral_ratio_d12 ? effective_collateral_ratio_d12 : global_collateral_ratio_d12;

        uint256 expectedBdxValue_d18 = ((BdPoolLibrary.COLLATERAL_RATIO_MAX - cr) * totalSupply()) / BdPoolLibrary.COLLATERAL_RATIO_PRECISION;

        if (expectedBdxValue_d18 == 0) {
            return BdPoolLibrary.COLLATERAL_RATIO_MAX; // in we need no BDX, the coverage is 100%
        }

        uint256 bdxPrice_d12 = oracle_price(PriceChoice.BDX);

        if (bdxPrice_d12 == 0) {
            return 0; // in we need BDX but BDX price is 0, the coverage is 0%
        }

        uint256 expectedBdx_d18 = (expectedBdxValue_d18 * BdPoolLibrary.PRICE_PRECISION) / bdxPrice_d12;

        uint256 bdxSupply_d18 = BDX.balanceOf(address(this)) - unclaimedPoolsBDX;
        uint256 effectiveBdxCR_d12 = (BdPoolLibrary.PRICE_PRECISION * bdxSupply_d18) / expectedBdx_d18;

        return effectiveBdxCR_d12 > BdPoolLibrary.COLLATERAL_RATIO_MAX ? BdPoolLibrary.COLLATERAL_RATIO_MAX : effectiveBdxCR_d12;
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    // Used by pools when user redeems
    function pool_burn_from(address b_address, uint256 b_amount) external onlyPools {
        burnFrom(b_address, b_amount);

        emit BdStableBurned(b_address, msg.sender, b_amount);
    }

    // This function is what other bd pools will call to mint new bd stable
    function pool_mint(address m_address, uint256 m_amount) external onlyPools {
        super._mint(m_address, m_amount);

        lastMintByUserBlock[m_address] = block.number;

        emit BdStableMinted(msg.sender, m_address, m_amount);
    }

    // Adds collateral addresses supported, such as tether and busd, must be ERC20
    function addPool(address pool_address) external onlyOwner {
        require(bdstable_pools[pool_address] == false, "pool already exists");
        require(bdstable_pools_array.length < MAX_NUMBER_OF_POOLS, "pools limit reached");

        bdstable_pools[pool_address] = true;
        bdstable_pools_array.push(payable(pool_address));

        emit PoolAdded(pool_address);
    }

    // Remove a pool
    function removePool(address pool_address) external onlyOwner {
        require(bdstable_pools[pool_address] == true, "address doesn't exist already");

        delete bdstable_pools[pool_address];

        // bdstable_pools_array.length is limited by addPool function
        for (uint256 i = 0; i < bdstable_pools_array.length; i++) {
            if (bdstable_pools_array[i] == pool_address) {
                bdstable_pools_array[i] = bdstable_pools_array[bdstable_pools_array.length - 1];
                bdstable_pools_array.pop();
                break;
            }
        }

        emit PoolRemoved(pool_address);
    }

    function setBDStable_WETH_Oracle(address _bdstable_oracle_addr, address _weth_address) external onlyOwner {
        require(_bdstable_oracle_addr != address(0), "Oracle cannot be set to the zero address");
        require(_weth_address != address(0), "WETH cannot be set to the zero address");

        bdstableWethOracle = ICryptoPairOracle(_bdstable_oracle_addr);
        weth_address = _weth_address;

        emit BDStableWETHOracleSet(_bdstable_oracle_addr, _weth_address);
    }

    function setBDX_WETH_Oracle(address _bdx_oracle_addr, address _weth_address) external onlyOwner {
        require(_bdx_oracle_addr != address(0), "Oracle cannot be set to the zero address");
        require(_weth_address != address(0), "WETH cannot be set to the zero address");

        bdxWethOracle = ICryptoPairOracle(_bdx_oracle_addr);
        weth_address = _weth_address;

        emit BDXWETHOracleSet(_bdx_oracle_addr, _weth_address);
    }

    function setETH_fiat_Oracle(address _eth_fiat_consumer_address) external onlyOwner {
        require(_eth_fiat_consumer_address != address(0), "Oracle cannot be set to the zero address");

        weth_fiat_pricer = IOracleBasedCryptoFiatFeed(_eth_fiat_consumer_address);

        emit EthFiatOracleSet(_eth_fiat_consumer_address);
    }

    function setBdStable_step_d12(uint256 _bdStable_step_d12) external onlyOwner {
        bdStable_step_d12 = _bdStable_step_d12;

        emit BdStableStepSet(_bdStable_step_d12);
    }

    function set_price_target_d12(uint256 _price_target_d12) external onlyOwner {
        price_target_d12 = _price_target_d12;

        emit PriceTargetSet(_price_target_d12);
    }

    function set_price_band_d12(uint256 _price_band_d12) external onlyOwner {
        price_band_d12 = _price_band_d12;

        emit PriceBandSet(_price_band_d12);
    }

    function toggleCollateralRatioPaused() external onlyOwner {
        collateral_ratio_paused = !collateral_ratio_paused;

        emit CollateralRatioPausedToggled(collateral_ratio_paused);
    }

    function lockCollateralRatioAt(uint256 wantedCR_d12) external onlyOwner {
        require(wantedCR_d12 <= BdPoolLibrary.COLLATERAL_RATIO_MAX, "CR must be <0;1>");

        global_collateral_ratio_d12 = wantedCR_d12;
        collateral_ratio_paused = true;

        emit CollateralRatioLocked(wantedCR_d12);
    }

    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;
        emit TreasuryChanged(_treasury);
    }

    function setMinimumSwapsDelayInBlocks(uint256 _minimumMintRedeemDelayInBlocks) external onlyOwner {
        minimumMintRedeemDelayInBlocks = _minimumMintRedeemDelayInBlocks;
        emit MinimumMintRedeemDelayInBlocksSet(_minimumMintRedeemDelayInBlocks);
    }

    function pool_claim_bdx(uint256 amount) external onlyPools {
        unclaimedPoolsBDX = unclaimedPoolsBDX + amount;
    }

    function pool_transfer_claimed_bdx(address to, uint256 amount) external onlyPools {
        unclaimedPoolsBDX = unclaimedPoolsBDX - amount;
        BDX.safeTransfer(to, amount);
    }

    function transfer_bdx(address to, uint256 BDX_amount) external onlyOwnerOrPool {
        require((BDX.balanceOf(address(this)) - unclaimedPoolsBDX) >= BDX_amount, "Not enough BDX");

        BDX.safeTransfer(to, BDX_amount);
    }

    function transfer_bdx_force(address to, uint256 BDX_amount) external onlyOwner {
        BDX.safeTransfer(to, BDX_amount);
    }

    function burnFrom(address account, uint256 amount) public virtual {
        uint256 permittedAllowance = allowance(account, _msgSender());
        require(amount <= permittedAllowance, "ERC20: burn amount exceeds allowance");

        uint256 decreasedAllowance = permittedAllowance - amount;
        super._approve(account, _msgSender(), decreasedAllowance);
        super._burn(account, amount);
    }

    /* ========== EVENTS ========== */

    event CollateralRatioRefreshed(uint256 global_collateral_ratio);
    event BdStableBurned(address indexed from, address indexed to, uint256 amount);
    event BdStableMinted(address indexed from, address indexed to, uint256 amount);
    event PoolAdded(address pool_address);
    event PoolRemoved(address pool_address);
    event BDStableWETHOracleSet(address indexed bdstable_oracle_addr, address indexed weth_address);
    event BDXWETHOracleSet(address indexed bdx_oracle_address, address indexed weth_address);
    event EthFiatOracleSet(address eth_fiat_consumer_address);
    event BdStableStepSet(uint256 bdStable_step_d12);
    event PriceBandSet(uint256 _price_band_d12);
    event PriceTargetSet(uint256 _price_target_d12);
    event CollateralRatioPausedToggled(bool collateral_ratio_paused);
    event CollateralRatioLocked(uint256 lockedCR_d12);
    event MinimumMintRedeemDelayInBlocksSet(uint256 minimumMintRedeemDelayInBlocks);
    event TreasuryChanged(address newTreasury);
}
","keccak256":"0x00467183e22136c87c9433a1f5d03b9df590ac7541a70a0ea932c8091da2f53e","license":"MIT"},"contracts/BdStable/Pools/BdPoolLibrary.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

library BdPoolLibrary {
    // Constants for various precisions
    uint256 public constant PRICE_PRECISION = 1e12;
    uint256 public constant COLLATERAL_RATIO_PRECISION = 1e12;
    uint256 public constant COLLATERAL_RATIO_MAX = 1e12;

    // ================ Functions ================

    function calcMint1t1BD(uint256 col_price, uint256 collateral_amount_d18) external pure returns (uint256) {
        return (collateral_amount_d18 * col_price) / PRICE_PRECISION;
    }

    function calcMintAlgorithmicBD(uint256 bdx_price_fiat_d12, uint256 bdx_amount_d18) external pure returns (uint256) {
        return (bdx_amount_d18 * bdx_price_fiat_d12) / PRICE_PRECISION;
    }

    // Must be internal because of the struct
    function calcMintFractionalBD(
        uint256 bdx_price_fiat_d12,
        uint256 col_price_fiat_d12,
        uint256 collateral_amount_d18,
        uint256 col_ratio_d12
    ) internal pure returns (uint256, uint256) {
        // Since solidity truncates division, every division operation must be the last operation in the equation to ensure minimum error
        // The contract must check the proper ratio was sent to mint BdStable. We do this by seeing the minimum mintable BdStable based on each amount

        uint256 c_fiat_value_d18 = (collateral_amount_d18 * col_price_fiat_d12) / PRICE_PRECISION;

        uint256 calculated_bdx_fiat_value_d18 = ((c_fiat_value_d18 * COLLATERAL_RATIO_PRECISION) / col_ratio_d12) - c_fiat_value_d18;

        uint256 calculated_bdx_needed = (calculated_bdx_fiat_value_d18 * PRICE_PRECISION) / bdx_price_fiat_d12;

        return (c_fiat_value_d18 + calculated_bdx_fiat_value_d18, calculated_bdx_needed);
    }

    function calcRecollateralizeBdStableInner(
        uint256 collateral_amount_d18,
        uint256 col_price,
        uint256 global_collat_value,
        uint256 bdStable_total_supply,
        uint256 global_collateral_ratio
    ) external pure returns (uint256, uint256) {
        uint256 collat_value_attempted = (collateral_amount_d18 * col_price) / PRICE_PRECISION;
        uint256 effective_collateral_ratio = (global_collat_value * PRICE_PRECISION) / bdStable_total_supply; //returns it in 1e12
        uint256 recollat_possible = ((global_collateral_ratio - effective_collateral_ratio) * bdStable_total_supply) / COLLATERAL_RATIO_PRECISION;

        uint256 amount_to_recollat;
        if (collat_value_attempted <= recollat_possible) {
            amount_to_recollat = collat_value_attempted;
        } else {
            amount_to_recollat = recollat_possible;
        }

        return ((amount_to_recollat * PRICE_PRECISION) / col_price, amount_to_recollat);
    }

    // Must be internal because of the struct
    function calcBuyBackBDX(
        uint256 excess_collateral_fiat_value_d18,
        uint256 bdx_price_fiat_d12,
        uint256 col_price_fiat_d12,
        uint256 bdx_amount_d18
    ) internal pure returns (uint256) {
        // If the total collateral value is higher than the amount required at the current collateral ratio then buy back up to the possible BDX with the desired collateral
        require(excess_collateral_fiat_value_d18 > 0, "No excess collateral to buy back!");

        // Make sure not to take more than is available
        uint256 bdx_fiat_value_d18 = (bdx_amount_d18 * bdx_price_fiat_d12) / PRICE_PRECISION;
        require(bdx_fiat_value_d18 <= excess_collateral_fiat_value_d18, "You are trying to buy back more than the excess!");

        // Get the equivalent amount of collateral based on the market value of BDX provided
        uint256 collateral_equivalent_d18 = (bdx_fiat_value_d18 * PRICE_PRECISION) / col_price_fiat_d12;

        return (collateral_equivalent_d18);
    }
}
","keccak256":"0x67b3ad8de372b7f4061af76235a6808d3f403c3cba34f95c7df21982acb71f1b","license":"MIT"},"contracts/BdStable/Pools/BdStablePool.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../../BdStable/BDStable.sol";
import "../../Oracle/ICryptoPairOracle.sol";
import "./BdPoolLibrary.sol";
import "../../Utils/IWETH.sol";

contract BdStablePool is OwnableUpgradeable {
    using SafeERC20 for IERC20;

    /* ========== STATE VARIABLES ========== */

    IERC20 private BDX;
    IWETH private NativeTokenWrapper;

    IERC20 public collateral_token;
    BDStable public BDSTABLE;
    ICryptoPairOracle public collatWEthOracle;

    bool public is_collateral_wrapping_native_token;

    uint256 private missing_decimals; // Number of decimals needed to get to 18
    address private weth_address;

    mapping(address => uint256) public redeemBDXBalances;
    mapping(address => uint256) public redeemCollateralBalances;
    uint256 public unclaimedPoolCollateral;
    mapping(address => uint256) public lastRedeemed;

    // AccessControl state variables
    bool public mintPaused;
    bool public redeemPaused;
    bool public recollateralizePaused;
    bool public buyBackPaused;
    bool public collateralPricePaused;
    bool public recollateralizeOnlyForOwner;
    bool public buybackOnlyForOwner;

    uint256 public minting_fee; //d12
    uint256 public redemption_fee; //d12
    uint256 public buyback_fee; //d12
    uint256 public recollat_fee; //d12

    // Pool_ceiling is the total units of collateral that a pool contract can hold
    uint256 public pool_ceiling; // d18

    // Stores price of the collateral, if price is paused
    uint256 public pausedPrice;

    // Bonus rate on BDX minted during recollateralizeBdStable(); 12 decimals of precision, set to 0.75% on genesis
    uint256 public bonus_rate; // d12

    // Number of blocks to wait before being able to collectRedemption()
    uint256 public redemption_delay;

    /* ========== MODIFIERS ========== */

    modifier notRedeemPaused() {
        require(redeemPaused == false, "Redeeming is paused");
        _;
    }

    modifier notMintPaused() {
        require(mintPaused == false, "Minting is paused");
        _;
    }

    /* ========== CONSTRUCTOR ========== */

    function initialize(
        address _bdstable_contract_address,
        address _bdx_contract_address,
        address _collateral_address,
        uint256 _collateral_decimals,
        bool _is_collateral_wrapping_native_token
    ) external initializer {
        require(_bdstable_contract_address != address(0), "BdStable address cannot be 0");
        require(_bdx_contract_address != address(0), "BDX address cannot be 0");
        require(_collateral_address != address(0), "Collateral address cannot be 0");

        __Ownable_init();

        BDSTABLE = BDStable(_bdstable_contract_address);
        BDX = IERC20(_bdx_contract_address);
        if (_is_collateral_wrapping_native_token) {
            NativeTokenWrapper = IWETH(_collateral_address);
        }
        collateral_token = IERC20(_collateral_address);
        missing_decimals = uint256(18) - _collateral_decimals;

        is_collateral_wrapping_native_token = _is_collateral_wrapping_native_token;

        pool_ceiling = 1e36; // d18
        bonus_rate = 7500000000; // d12 0.75%
        redemption_delay = 1;
        minting_fee = 3000000000; // d12 0.3%
        redemption_fee = 3000000000; // d12 0.3%

        recollateralizeOnlyForOwner = false;
        buybackOnlyForOwner = true;
    }

    /* ========== VIEWS ========== */

    // Returns the price of the pool collateral in fiat
    function getCollateralPrice_d12() public view returns (uint256) {
        if (collateralPricePaused == true) {
            return pausedPrice;
        } else {
            uint256 eth_fiat_price_d12 = BDSTABLE.weth_fiat_price();
            uint256 collat_eth_price = collatWEthOracle.consult(weth_address, BdPoolLibrary.PRICE_PRECISION);

            return (eth_fiat_price_d12 * BdPoolLibrary.PRICE_PRECISION) / collat_eth_price;
        }
    }

    // Returns fiat value of collateral held in this BdStable pool
    function collatFiatBalance() external view returns (uint256) {
        //Expressed in collateral token decimals
        if (collateralPricePaused == true) {
            return
                ((collateral_token.balanceOf(address(this)) - unclaimedPoolCollateral) * (10**missing_decimals) * pausedPrice) /
                BdPoolLibrary.PRICE_PRECISION;
        } else {
            return
                ((collateral_token.balanceOf(address(this)) - unclaimedPoolCollateral) * (10**missing_decimals) * getCollateralPrice_d12()) /
                BdPoolLibrary.PRICE_PRECISION;
        }
    }

    /* ========== PUBLIC FUNCTIONS ========== */

    function updateOraclesIfNeeded() public {
        BDSTABLE.updateOraclesIfNeeded();
        if (collatWEthOracle.shouldUpdateOracle()) {
            collatWEthOracle.updateOracle();
        }
    }

    // Will fail if fully collateralized or fully algorithmic
    // > 0% and < 100% collateral-backed
    function mintFractionalBdStable(
        uint256 collateral_amount_in_max,
        uint256 bdx_in_max,
        uint256 bdStable_out_min,
        bool useNativeToken
    ) external payable notMintPaused {
        if (useNativeToken) {
            require(is_collateral_wrapping_native_token, "Pool doesn't support native token");
            require(msg.value == collateral_amount_in_max, "msg.value and collateral_amount_in_max do not match");
        }

        updateOraclesIfNeeded();
        uint256 bdx_price = BDSTABLE.BDX_price_d12();
        uint256 global_collateral_ratio_d12 = BDSTABLE.global_collateral_ratio_d12();

        if (global_collateral_ratio_d12 == 0) {
            collateral_amount_in_max = 0;
        } else if (global_collateral_ratio_d12 == BdPoolLibrary.COLLATERAL_RATIO_MAX) {
            bdx_in_max = 0;
        }

        require(
            (collateral_token.balanceOf(address(this)) - unclaimedPoolCollateral + collateral_amount_in_max) <= pool_ceiling,
            "Pool ceiling reached, no more BdStable can be minted with this collateral"
        );

        uint256 collateral_amount_in_max_d18 = collateral_amount_in_max * (10**missing_decimals);

        uint256 mint_amount;
        uint256 bdx_needed;
        if (global_collateral_ratio_d12 == 0) {
            mint_amount = BdPoolLibrary.calcMintAlgorithmicBD(bdx_price, bdx_in_max);
            bdx_needed = bdx_in_max;
        } else if (global_collateral_ratio_d12 == 1) {
            mint_amount = BdPoolLibrary.calcMint1t1BD(getCollateralPrice_d12(), collateral_amount_in_max_d18);
            bdx_needed = 0;
        } else {
            (mint_amount, bdx_needed) = BdPoolLibrary.calcMintFractionalBD(
                bdx_price,
                getCollateralPrice_d12(),
                collateral_amount_in_max_d18,
                global_collateral_ratio_d12
            );
        }

        mint_amount = (mint_amount * (uint256(BdPoolLibrary.PRICE_PRECISION) - minting_fee)) / BdPoolLibrary.PRICE_PRECISION;

        require(bdStable_out_min <= mint_amount, "Slippage limit reached");
        require(bdx_needed <= bdx_in_max, "Not enough BDX inputted");

        BDSTABLE.refreshCollateralRatio();

        if (bdx_needed > 0) {
            BDX.safeTransferFrom(msg.sender, address(BDSTABLE), bdx_needed);
        }

        if (collateral_amount_in_max > 0) {
            if (useNativeToken) {
                NativeTokenWrapper.deposit{value: collateral_amount_in_max}();
            } else {
                collateral_token.safeTransferFrom(msg.sender, address(this), collateral_amount_in_max);
            }
        }

        BDSTABLE.pool_mint(msg.sender, mint_amount);
    }

    // Will fail if fully collateralized or algorithmic
    // Redeem BDSTABLE for collateral and BDX. > 0% and < 100% collateral-backed
    function redeemFractionalBdStable(
        uint256 BdStable_amount,
        uint256 BDX_out_min,
        uint256 COLLATERAL_out_min
    ) external notRedeemPaused {
        updateOraclesIfNeeded();
        uint256 global_collateral_ratio_d12 = BDSTABLE.global_collateral_ratio_d12();
        uint256 effective_global_collateral_ratio_d12 = BDSTABLE.effective_global_collateral_ratio_d12();

        uint256 cr_d12 = effective_global_collateral_ratio_d12 < global_collateral_ratio_d12
            ? effective_global_collateral_ratio_d12
            : global_collateral_ratio_d12;

        uint256 BdStable_amount_post_fee = (BdStable_amount * (uint256(BdPoolLibrary.PRICE_PRECISION) - redemption_fee)) /
            BdPoolLibrary.PRICE_PRECISION;

        uint256 bdx_fiat_value_d18 = BdStable_amount_post_fee - ((BdStable_amount_post_fee * cr_d12) / BdPoolLibrary.PRICE_PRECISION);

        uint256 bdx_amount = (bdx_fiat_value_d18 * BdPoolLibrary.PRICE_PRECISION) / BDSTABLE.BDX_price_d12();
        uint256 bdx_coverage_ratio = BDSTABLE.get_effective_bdx_coverage_ratio();
        bdx_amount = (bdx_amount * bdx_coverage_ratio) / BdPoolLibrary.COLLATERAL_RATIO_PRECISION;

        // Need to adjust for decimals of collateral
        uint256 BdStable_amount_precision = BdStable_amount_post_fee / (10**missing_decimals);
        uint256 collateral_fiat_value = (BdStable_amount_precision * cr_d12) / BdPoolLibrary.PRICE_PRECISION;
        uint256 collateral_needed = (collateral_fiat_value * BdPoolLibrary.PRICE_PRECISION) / getCollateralPrice_d12();

        require(collateral_needed <= collateral_token.balanceOf(address(this)) - unclaimedPoolCollateral, "Not enough collateral in pool");
        require(COLLATERAL_out_min <= collateral_needed, "Slippage limit reached [collateral]");
        require(BDX_out_min <= bdx_amount, "Slippage limit reached [BDX]");
        require(BDSTABLE.canLegallyRedeem(msg.sender), "Cannot legally redeem");

        redeemCollateralBalances[msg.sender] = redeemCollateralBalances[msg.sender] + collateral_needed;

        unclaimedPoolCollateral = unclaimedPoolCollateral + collateral_needed;

        BDSTABLE.refreshCollateralRatio();

        if (bdx_amount > 0) {
            require(BDSTABLE.canLegallyRedeem(msg.sender), "Cannot legally redeem");

            redeemBDXBalances[msg.sender] = redeemBDXBalances[msg.sender] + bdx_amount;

            BDSTABLE.pool_claim_bdx(bdx_amount);
        }

        lastRedeemed[msg.sender] = block.number;

        // Move all external functions to the end
        BDSTABLE.pool_burn_from(msg.sender, BdStable_amount);
    }

    // After a redemption happens, transfer the newly minted BDX and owed collateral from this pool
    // contract to the user. Redemption is split into two functions to prevent flash loans from being able
    // to take out BdStable/collateral from the system, use an AMM to trade the new price, and then mint back into the system.
    function collectRedemption(bool useNativeToken) external {
        require((lastRedeemed[msg.sender] + redemption_delay) <= block.number, "Must wait for redemption_delay blocks before collecting redemption");
        bool sendBDX = false;
        bool sendCollateral = false;
        uint256 BDXAmount;
        uint256 CollateralAmount;

        // Use Checks-Effects-Interactions pattern
        if (redeemBDXBalances[msg.sender] > 0) {
            BDXAmount = redeemBDXBalances[msg.sender];
            redeemBDXBalances[msg.sender] = 0;

            sendBDX = true;
        }

        if (redeemCollateralBalances[msg.sender] > 0) {
            CollateralAmount = redeemCollateralBalances[msg.sender];
            redeemCollateralBalances[msg.sender] = 0;
            unclaimedPoolCollateral = unclaimedPoolCollateral - CollateralAmount;

            sendCollateral = true;
        }

        if (sendBDX == true) {
            BDSTABLE.pool_transfer_claimed_bdx(msg.sender, BDXAmount);
        }
        if (sendCollateral == true) {
            if (useNativeToken) {
                NativeTokenWrapper.withdraw(CollateralAmount);
                safeTransferETH(msg.sender, CollateralAmount);
            } else {
                collateral_token.safeTransfer(msg.sender, CollateralAmount);
            }
        }
    }

    // When the protocol is recollateralizing, we need to give a discount of BDX to hit the new CR target
    // Thus, if the target collateral ratio is higher than the actual value of collateral, minters get BDX for adding collateral
    // This function simply rewards anyone that sends collateral to a pool with the same amount of BDX + the bonus rate
    // Anyone can call this function to recollateralize the protocol and take the extra BDX value from the bonus rate as an arb opportunity
    function recollateralizeBdStable(
        uint256 collateral_amount,
        uint256 BDX_out_min,
        bool useNativeToken
    ) external payable {
        require(recollateralizePaused == false, "Recollateralize is paused");

        if (recollateralizeOnlyForOwner) {
            require(msg.sender == owner(), "Currently only owner can recollateralize");
        }

        if (useNativeToken) {
            require(is_collateral_wrapping_native_token, "Pool doesn't support native token");
            require(msg.value == collateral_amount, "msg.value and collateral_amount do not match");
        }

        updateOraclesIfNeeded();
        uint256 collateral_amount_d18 = collateral_amount * (10**missing_decimals);
        uint256 bdx_price = BDSTABLE.BDX_price_d12();
        uint256 bdStable_total_supply = BDSTABLE.totalSupply();
        uint256 global_collateral_ratio_d12 = BDSTABLE.global_collateral_ratio_d12();
        uint256 global_collat_value = BDSTABLE.globalCollateralValue();

        (uint256 collateral_units, uint256 amount_to_recollat) = BdPoolLibrary.calcRecollateralizeBdStableInner(
            collateral_amount_d18,
            getCollateralPrice_d12(),
            global_collat_value,
            bdStable_total_supply,
            global_collateral_ratio_d12
        );

        uint256 collateral_units_precision = collateral_units / (10**missing_decimals);

        uint256 bdx_paid_back = (amount_to_recollat * (uint256(BdPoolLibrary.PRICE_PRECISION) + bonus_rate - recollat_fee)) / bdx_price;
        uint256 bdx_coverage_ratio = BDSTABLE.get_effective_bdx_coverage_ratio();
        bdx_paid_back = (bdx_paid_back * bdx_coverage_ratio) / BdPoolLibrary.COLLATERAL_RATIO_PRECISION;

        require(BDX_out_min <= bdx_paid_back, "Slippage limit reached");

        BDSTABLE.refreshCollateralRatio();

        if (useNativeToken) {
            // no need to check collateral_units_precision, it's <= then collateral_amount

            NativeTokenWrapper.deposit{value: collateral_units_precision}();

            // refund remaining native token, if any left
            if (msg.value > collateral_units_precision) {
                safeTransferETH(msg.sender, msg.value - collateral_units_precision);
            }
        } else {
            collateral_token.safeTransferFrom(msg.sender, address(this), collateral_units_precision);
        }

        if (bdx_paid_back > 0) {
            BDSTABLE.transfer_bdx(msg.sender, bdx_paid_back);
        }

        emit Recollateralized(collateral_units_precision, bdx_paid_back);
    }

    // Function can be called by an BDX holder to have the protocol buy back BDX with excess collateral value from a desired collateral pool
    // This can also happen if the collateral ratio > 1
    function buyBackBDX(
        uint256 BDX_amount,
        uint256 COLLATERAL_out_min,
        bool useNativeToken
    ) external {
        require(buyBackPaused == false, "Buyback is paused");

        if (buybackOnlyForOwner) {
            require(msg.sender == owner(), "Currently only owner can buyback");
        }

        updateOraclesIfNeeded();

        uint256 bdx_price = BDSTABLE.BDX_price_d12();

        uint256 collateral_equivalent_d18 = (BdPoolLibrary.calcBuyBackBDX(
            BDSTABLE.availableExcessCollatDV(),
            bdx_price,
            getCollateralPrice_d12(),
            BDX_amount
        ) * (uint256(BdPoolLibrary.PRICE_PRECISION) - buyback_fee)) / BdPoolLibrary.PRICE_PRECISION;

        uint256 collateral_precision = collateral_equivalent_d18 / (10**missing_decimals);

        require(COLLATERAL_out_min <= collateral_precision, "Slippage limit reached");

        // Take bdx from sender
        BDX.safeTransferFrom(msg.sender, address(BDSTABLE), BDX_amount);

        if (useNativeToken) {
            // Give the sender their desired collateral
            NativeTokenWrapper.withdraw(collateral_precision);
            safeTransferETH(msg.sender, collateral_precision);
        } else {
            // Give the sender their desired collateral
            collateral_token.safeTransfer(msg.sender, collateral_precision);
        }

        emit BoughtBack(BDX_amount, collateral_precision);
    }

    receive() external payable {
        require(msg.sender == address(NativeTokenWrapper), "Only native token wrapper allowed to send native token");
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function setCollatWETHOracle(address _collateral_weth_oracle_address, address _weth_address) external onlyOwner {
        require(_collateral_weth_oracle_address != address(0), "Oracle cannot be set to the zero address");
        require(_weth_address != address(0), "WETH cannot be set to the zero address");

        collatWEthOracle = ICryptoPairOracle(_collateral_weth_oracle_address);
        weth_address = _weth_address;

        emit CollateralWethOracleSet(_collateral_weth_oracle_address, _weth_address);
    }

    function toggleMintingPaused() external onlyOwner {
        mintPaused = !mintPaused;

        emit MintingPausedToggled(mintPaused);
    }

    function toggleRedeemingPaused() external onlyOwner {
        redeemPaused = !redeemPaused;

        emit RedeemingPausedToggled(redeemPaused);
    }

    function toggleRecollateralizePaused() external onlyOwner {
        recollateralizePaused = !recollateralizePaused;

        emit RecollateralizePausedToggled(recollateralizePaused);
    }

    function toggleBuybackPaused() external onlyOwner {
        buyBackPaused = !buyBackPaused;

        emit BuybackPausedToggled(buyBackPaused);
    }

    function toggleBuybackOnlyForOwner() external onlyOwner {
        buybackOnlyForOwner = !buybackOnlyForOwner;

        emit BuybackOnlyForOwnerToggled(buybackOnlyForOwner);
    }

    function toggleRecollateralizeOnlyForOwner() external onlyOwner {
        recollateralizeOnlyForOwner = !recollateralizeOnlyForOwner;

        emit RecollateralizeOnlyForOwnerToggled(recollateralizeOnlyForOwner);
    }

    function toggleCollateralPricePaused(uint256 _new_price) external onlyOwner {
        // If pausing, set paused price; else if unpausing, clear pausedPrice
        if (collateralPricePaused == false) {
            pausedPrice = _new_price;
        } else {
            pausedPrice = 0;
        }
        collateralPricePaused = !collateralPricePaused;

        emit CollateralPriceToggled(collateralPricePaused);
    }

    // Combined into one function due to 24KiB contract memory limit
    function setPoolParameters(
        uint256 new_ceiling,
        uint256 new_bonus_rate,
        uint256 new_redemption_delay,
        uint256 new_mint_fee,
        uint256 new_redeem_fee,
        uint256 new_buyback_fee,
        uint256 new_recollat_fee
    ) external onlyOwner {
        pool_ceiling = new_ceiling;
        bonus_rate = new_bonus_rate;
        redemption_delay = new_redemption_delay;
        minting_fee = new_mint_fee;
        redemption_fee = new_redeem_fee;
        buyback_fee = new_buyback_fee;
        recollat_fee = new_recollat_fee;

        emit PoolParametersSet(new_ceiling, new_bonus_rate, new_redemption_delay, new_mint_fee, new_redeem_fee, new_buyback_fee, new_recollat_fee);
    }

    function safeTransferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(success, "ETH transfer failed");
    }

    /* ========== EVENTS ========== */

    event PoolParametersSet(
        uint256 new_ceiling,
        uint256 new_bonus_rate,
        uint256 new_redemption_delay,
        uint256 new_mint_fee,
        uint256 new_redeem_fee,
        uint256 new_buyback_fee,
        uint256 new_recollat_fee
    );
    event MintingPausedToggled(bool toggled);
    event RedeemingPausedToggled(bool toggled);
    event RecollateralizePausedToggled(bool toggled);
    event BuybackPausedToggled(bool toggled);
    event CollateralPriceToggled(bool toggled);
    event CollateralWethOracleSet(address indexed collateral_weth_oracle_address, address indexed weth_address);
    event RecollateralizeOnlyForOwnerToggled(bool recollateralizeOnlyForOwner);
    event BuybackOnlyForOwnerToggled(bool buybackOnlyForOwner);
    event Recollateralized(uint256 indexed collateral_amount_paid, uint256 indexed bdx_paid_back);
    event BoughtBack(uint256 indexed bdx_amount_paid, uint256 indexed collateral_paid_back);
}
","keccak256":"0x32da2e8b3ecba8555954df0ece030ca81d3674e04f559edf52c7d6902eb2a1fc","license":"MIT"},"contracts/Bdx/BDXShares.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../BdStable/BDStable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract BDXShares is ERC20Upgradeable, OwnableUpgradeable {
    /* ========== STATE VARIABLES ========== */
    uint256 public constant MAX_TOTAL_SUPPLY = 21 * 1e6 * 1e18;

    mapping(address => bool) public mappingBdstables;
    address[] public bdstables;

    /* ========== CONSTRUCTOR ========== */

    function initialize(string memory _name, string memory _symbol) external initializer {
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(bytes(_symbol).length > 0, "Symbol cannot be empty");

        __ERC20_init(_name, _symbol);
        __Ownable_init();
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function getBdStablesLength() external view returns (uint256) {
        return bdstables.length;
    }

    function addBdStableAddress(address bdstable_contract_address) external onlyOwner {
        if (!mappingBdstables[bdstable_contract_address]) {
            mappingBdstables[bdstable_contract_address] = true;
            bdstables.push(bdstable_contract_address);
            emit BdStableAddressAdded(bdstable_contract_address);
        }
    }

    function mint(address to, uint256 amount) public onlyOwner {
        require((totalSupply() + amount) <= MAX_TOTAL_SUPPLY, "BDX limit reached");

        _mint(to, amount);

        emit BdxMinted(address(this), to, amount);
    }

    /* ========== EVENTS ========== */

    event BdxBurned(address indexed from, address indexed to, uint256 indexed amount);
    event BdxMinted(address indexed from, address indexed to, uint256 indexed amount);
    event BdStableAddressAdded(address indexed addr);
}
