// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "./StakingRewardsDistribution.sol";

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract Vesting is OwnableUpgradeable {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    struct VestingSchedule {
        uint256 vestingStartedTimeStamp;
        uint256 vestingEndTimeStamp;
        uint256 totalVestedAmount_d18;
        uint256 releasedAmount_d18;
    }

    mapping(address => VestingSchedule[]) public vestingSchedules;

    address public vestingScheduler;
    address public fundsProvider;
    uint256 public vestingTimeInSeconds;
    IERC20 public vestedToken;

    function initialize(
        address _vestedTokenAddress,
        address _vestingScheduler,
        address _fundsProvider,
        uint256 _vestingTimeInSeconds
    ) external initializer {
        require(_vestedTokenAddress != address(0), "Vesting address cannot be 0");
        require(_vestingScheduler != address(0), "VestingScheduler address cannot be 0");
        require(_fundsProvider != address(0), "FundsProvider address cannot be 0");
        require(_vestingTimeInSeconds > 0, "Vesting timme cannot be set to 0");

        __Ownable_init();

        vestedToken = IERC20(_vestedTokenAddress);
        vestingScheduler = _vestingScheduler;
        fundsProvider = _fundsProvider;
        vestingTimeInSeconds = _vestingTimeInSeconds;
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function schedule(address _receiver, uint256 _amount_d18) external {
        // to prevent melicious users form cloging user's schedules
        require(msg.sender == vestingScheduler, "Only vesting scheduler can create vesting schedules");

        vestingSchedules[_receiver].push(VestingSchedule(block.timestamp, block.timestamp.add(vestingTimeInSeconds), _amount_d18, 0));

        vestedToken.safeTransferFrom(fundsProvider, address(this), _amount_d18);

        emit ScheduleCreated(_receiver, _amount_d18);
    }

    function claim(uint256 from, uint256 to) external {
        VestingSchedule[] storage userVestingSchedules = vestingSchedules[msg.sender];

        uint256 rewardsToClaim = 0;
        uint256 userVestingSchedulesCount = userVestingSchedules.length;
        for (uint256 i = from; i < to && i < userVestingSchedulesCount; i++) {
            if (isFullyVested(userVestingSchedules[i])) {
                rewardsToClaim = rewardsToClaim.add(userVestingSchedules[i].totalVestedAmount_d18.sub(userVestingSchedules[i].releasedAmount_d18));

                userVestingSchedulesCount--;
                userVestingSchedules[i] = userVestingSchedules[userVestingSchedulesCount];
                userVestingSchedules.pop();
                i--;
            } else {
                uint256 proprtionalReward = getAvailableReward(userVestingSchedules[i]);
                rewardsToClaim = rewardsToClaim.add(proprtionalReward);
                userVestingSchedules[i].releasedAmount_d18 = userVestingSchedules[i].releasedAmount_d18.add(proprtionalReward);
            }
        }

        vestedToken.safeTransfer(msg.sender, rewardsToClaim);

        emit RewardClaimed(msg.sender, rewardsToClaim);
    }

    /* ========== VIEWS ========== */

    function userVestingSchedulesCount(address user) external view returns (uint256) {
        return vestingSchedules[user].length;
    }

    function isFullyVested(VestingSchedule memory _schedule) public view returns (bool) {
        return _schedule.vestingEndTimeStamp <= block.timestamp;
    }

    function getAvailableReward(VestingSchedule memory _schedule) public view returns (uint256) {
        if (isFullyVested(_schedule)) {
            return _schedule.totalVestedAmount_d18.sub(_schedule.releasedAmount_d18);
        }
        return
            (_schedule.totalVestedAmount_d18.mul(block.timestamp.sub(_schedule.vestingStartedTimeStamp)).div(vestingTimeInSeconds)).sub(
                _schedule.releasedAmount_d18
            );
    }

    function vestingSchedulesOf(address account) external view returns (VestingSchedule[] memory) {
        return vestingSchedules[account];
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function setVestingScheduler(address _vestingScheduler) external onlyOwner {
        require(_vestingScheduler != address(0), "Vesting scheduler cannot be set to the zero address");

        vestingScheduler = _vestingScheduler;
    }

    function setVestingTimeInSeconds(uint256 _vestingTimeInSeconds) external onlyOwner {
        require(_vestingTimeInSeconds > 0, "Vesting timme cannot be set to 0");
        vestingTimeInSeconds = _vestingTimeInSeconds;

        emit VestingTimeInSecondsSet(_vestingTimeInSeconds);
    }

    function setFundsProvider(address _fundsProvider) external onlyOwner {
        require(_fundsProvider != address(0), "Funds provider cannot be set to the zero address");

        fundsProvider = _fundsProvider;
    }

    event ScheduleCreated(address user, uint256 amount);
    event RewardClaimed(address user, uint256 amount);
    event VestingTimeInSecondsSet(uint256 vestingTimeInSeconds);
}

contracts/Staking/StakingRewardsDistribu.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../Bdx/BDXShares.sol";
import "./Vesting.sol";
import "./StakingRewards.sol";

contract StakingRewardsDistribution is OwnableUpgradeable {
    using SafeMath for uint256;
    using SafeERC20Upgradeable for BDXShares;

    uint256 public TOTAL_BDX_SUPPLY;

    uint256 public constant HUNDRED_PERCENT = 100;
    uint256 public constant MAX_REWARD_FEE = 1e12;

    // BDX minting schedule
    // They sum up to 50% of TOTAL_BDX_SUPPLY
    //   as this much is reserved for liquidity mining rewards
    uint256 public constant BDX_MINTING_SCHEDULE_PRECISON = 1000;
    uint256 public BDX_MINTING_SCHEDULE_YEAR_1;
    uint256 public BDX_MINTING_SCHEDULE_YEAR_2;
    uint256 public BDX_MINTING_SCHEDULE_YEAR_3;
    uint256 public BDX_MINTING_SCHEDULE_YEAR_4;
    uint256 public BDX_MINTING_SCHEDULE_YEAR_5;

    uint256 public EndOfYear_1;
    uint256 public EndOfYear_2;
    uint256 public EndOfYear_3;
    uint256 public EndOfYear_4;
    uint256 public EndOfYear_5;

    uint256 public vestingRewardRatio_percent;
    uint256 public rewardFee_d12;

    BDXShares public rewardsToken;
    Vesting public vesting;
    address public treasury;

    mapping(address => uint256) public stakingRewardsWeights;
    address[] public stakingRewardsAddresses;
    uint256 public stakingRewardsWeightsTotal;

    function initialize(
        address _rewardsToken,
        address _vesting,
        address _treasury,
        uint256 _vestingRewardRatio_percent
    ) external initializer {
        require(_rewardsToken != address(0), "Rewards address cannot be 0");
        require(_vesting != address(0), "Vesting address cannot be 0");
        require(_treasury != address(0), "Treasury address cannot be 0");
        require(_vestingRewardRatio_percent <= 100, "VestingRewardRatio_percent must be <= 100");

        __Ownable_init();

        rewardsToken = BDXShares(_rewardsToken);
        vesting = Vesting(_vesting);
        treasury = _treasury;
        TOTAL_BDX_SUPPLY = rewardsToken.MAX_TOTAL_SUPPLY();

        BDX_MINTING_SCHEDULE_YEAR_1 = TOTAL_BDX_SUPPLY.mul(200).div(BDX_MINTING_SCHEDULE_PRECISON);
        BDX_MINTING_SCHEDULE_YEAR_2 = TOTAL_BDX_SUPPLY.mul(125).div(BDX_MINTING_SCHEDULE_PRECISON);
        BDX_MINTING_SCHEDULE_YEAR_3 = TOTAL_BDX_SUPPLY.mul(100).div(BDX_MINTING_SCHEDULE_PRECISON);
        BDX_MINTING_SCHEDULE_YEAR_4 = TOTAL_BDX_SUPPLY.mul(50).div(BDX_MINTING_SCHEDULE_PRECISON);
        BDX_MINTING_SCHEDULE_YEAR_5 = TOTAL_BDX_SUPPLY.mul(25).div(BDX_MINTING_SCHEDULE_PRECISON);

        EndOfYear_1 = block.timestamp + 365 days;
        EndOfYear_2 = block.timestamp + 2 * 365 days;
        EndOfYear_3 = block.timestamp + 3 * 365 days;
        EndOfYear_4 = block.timestamp + 4 * 365 days;
        EndOfYear_5 = block.timestamp + 5 * 365 days;

        vestingRewardRatio_percent = _vestingRewardRatio_percent;
        rewardFee_d12 = 1e11; // 10%
    }

    // Precision 1e18 for compatibility with ERC20 token
    function getRewardRatePerSecond(address _stakingRewardsAddress) external view returns (uint256) {
        uint256 yearSchedule = 0;

        if (block.timestamp < EndOfYear_1) {
            yearSchedule = BDX_MINTING_SCHEDULE_YEAR_1;
        } else if (block.timestamp < EndOfYear_2) {
            yearSchedule = BDX_MINTING_SCHEDULE_YEAR_2;
        } else if (block.timestamp < EndOfYear_3) {
            yearSchedule = BDX_MINTING_SCHEDULE_YEAR_3;
        } else if (block.timestamp < EndOfYear_4) {
            yearSchedule = BDX_MINTING_SCHEDULE_YEAR_4;
        } else if (block.timestamp < EndOfYear_5) {
            yearSchedule = BDX_MINTING_SCHEDULE_YEAR_5;
        } else {
            yearSchedule = 0;
        }

        uint256 bdxPerSecond = yearSchedule.mul(stakingRewardsWeights[_stakingRewardsAddress]).div(365 * 24 * 60 * 60).div(
            stakingRewardsWeightsTotal
        );

        return bdxPerSecond;
    }

    function registerPools(address[] calldata _stakingRewardsAddresses, uint256[] calldata _stakingRewardsWeights) external onlyOwner {
        require(_stakingRewardsAddresses.length == _stakingRewardsWeights.length, "Pools addresses and weights lengths should be the same");

        for (uint256 i = 0; i < _stakingRewardsAddresses.length; i++) {
            if (stakingRewardsWeights[_stakingRewardsAddresses[i]] == 0) {
                // to avoid duplicates
                stakingRewardsAddresses.push(_stakingRewardsAddresses[i]);
            }

            stakingRewardsWeightsTotal -= stakingRewardsWeights[_stakingRewardsAddresses[i]]; // to support override
            stakingRewardsWeights[_stakingRewardsAddresses[i]] = _stakingRewardsWeights[i];
            stakingRewardsWeightsTotal += _stakingRewardsWeights[i];
            emit PoolRegistered(_stakingRewardsAddresses[i], _stakingRewardsWeights[i]);
        }
    }

    function unregisterPool(
        address pool,
        uint256 from,
        uint256 to
    ) external onlyOwner {
        to = to < stakingRewardsAddresses.length ? to : stakingRewardsAddresses.length;

        stakingRewardsWeightsTotal -= stakingRewardsWeights[pool];
        stakingRewardsWeights[pool] = 0;

        for (uint256 i = from; i < to; i++) {
            if (stakingRewardsAddresses[i] == pool) {
                stakingRewardsAddresses[i] = stakingRewardsAddresses[stakingRewardsAddresses.length - 1];
                stakingRewardsAddresses.pop();

                emit PoolRemoved(pool);
                return;
            }
        }
    }

    function collectAllRewards(uint256 from, uint256 to) external {
        to = to < stakingRewardsAddresses.length ? to : stakingRewardsAddresses.length;

        uint256 totalFee;
        uint256 totalRewardToRelease;
        uint256 totalRewardToVest;
        for (uint256 i = from; i < to; i++) {
            StakingRewards stakingRewards = StakingRewards(stakingRewardsAddresses[i]);

            stakingRewards.updateUserReward(msg.sender);
            uint256 poolReward = stakingRewards.rewards(msg.sender);

            if (poolReward > 0) {
                uint256 rewardFee = poolReward.mul(rewardFee_d12).div(MAX_REWARD_FEE);
                uint256 userReward = poolReward.sub(rewardFee);

                uint256 immediatelyReleasedReward = calculateImmediateReward(userReward);
                uint256 vestedReward = userReward.sub(immediatelyReleasedReward);

                totalFee = totalFee.add(rewardFee);
                totalRewardToRelease = totalRewardToRelease.add(immediatelyReleasedReward);
                totalRewardToVest = totalRewardToVest.add(vestedReward);

                stakingRewards.releaseReward(msg.sender, immediatelyReleasedReward, vestedReward);
            }
        }

        if (totalRewardToRelease > 0 || totalRewardToVest > 0) {
            releaseReward(msg.sender, totalRewardToRelease, totalRewardToVest);
            rewardsToken.safeTransfer(treasury, totalFee);
        }
    }

    function setVestingRewardRatio(uint256 _vestingRewardRatio) external onlyOwner {
        require(_vestingRewardRatio <= 100, "VestingRewardRatio_percent must be <= 100");
        vestingRewardRatio_percent = _vestingRewardRatio;

        emit VestingRewardRatioSet(_vestingRewardRatio);
    }

    function calculateImmediateReward(uint256 reward) private view returns (uint256) {
        return reward.mul(HUNDRED_PERCENT.sub(vestingRewardRatio_percent)).div(HUNDRED_PERCENT);
    }

    function releaseReward(
        address to,
        uint256 rewardToRelease,
        uint256 rewardToVest
    ) private {
        rewardsToken.approve(address(vesting), rewardToVest);
        vesting.schedule(to, rewardToVest);

        rewardsToken.safeTransfer(to, rewardToRelease);
    }

    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;
        emit TreasuryChanged(_treasury);
    }

    function setRewardFee_d12(uint256 _rewardFee_d12) external onlyOwner {
        require(_rewardFee_d12 <= MAX_REWARD_FEE, "Reward fee cannot exceed 100%");
        rewardFee_d12 = _rewardFee_d12;
        emit RewardFeeChanged(_rewardFee_d12);
    }

    modifier onlyStakingRewards() {
        require(stakingRewardsWeights[msg.sender] > 0, "Only registered staking rewards contracts allowed");
        _;
    }

    // ---------- EVENTS ----------
    event PoolRemoved(address indexed pool);
    event PoolRegistered(address indexed stakingRewardsAddress, uint256 indexed stakingRewardsWeight);
    event VestingRewardRatioSet(uint256 vestingRewardRatio_percent);
    event TreasuryChanged(address newTreasury);
    event RewardFeeChanged(uint256 newRewardFee_d12);
}

