contracts/Utils/BlindexUpdater.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "../Oracle/SovrynSwapPriceFeed.sol";
import "../Oracle/FiatToFiatPseudoOracleFeed.sol";
import "../Oracle/UniswapOracle/UniswapPairOracle.sol";
import "../BdStable/BDStable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract BlindexUpdater is Ownable, ReentrancyGuard {
    address public updater;

    constructor(address _updater) {
        updater = _updater;
    }

    function update(
        address[] memory _sovrynOracles,
        uint256[] memory _sovrynPrices,
        address[] memory _fiatOracles,
        uint256[] memory _fiatPrices,
        address[] memory _uniswapOracles,
        address[] memory _BDStables
    ) external onlyUpdater nonReentrant {
        require(_sovrynOracles.length == _sovrynPrices.length, "Each sovryn oracle address needs its corresponding price");
        require(_fiatOracles.length == _fiatPrices.length, "Each fiat oracle address needs its corresponding price");

        for (uint256 i = 0; i < _sovrynOracles.length; i++) {
            SovrynSwapPriceFeed priceFeed = SovrynSwapPriceFeed(_sovrynOracles[i]);
            if (priceFeed.shouldUpdateOracleWithVerification()) {
                priceFeed.updateOracleWithVerification(_sovrynPrices[i]);
            }
        }

        for (uint256 i = 0; i < _fiatOracles.length; i++) {
            FiatToFiatPseudoOracleFeed priceFeed = FiatToFiatPseudoOracleFeed(_fiatOracles[i]);
            priceFeed.setPrice(_fiatPrices[i]);
        }

        for (uint256 i = 0; i < _uniswapOracles.length; i++) {
            UniswapPairOracle priceFeed = UniswapPairOracle(_uniswapOracles[i]);
            if (priceFeed.shouldUpdateOracle()) {
                priceFeed.updateOracle();
            }
        }

        for (uint256 i = 0; i < _BDStables.length; i++) {
            BDStable priceFeed = BDStable(_BDStables[i]);
            priceFeed.refreshCollateralRatio();
        }
    }

    function setUpdater(address newUpdater) external onlyOwner {
        require(newUpdater != address(0), "Updater cannot be set to the zero address");

        address oldUpdater = updater;
        updater = newUpdater;
        emit UpdaterChanged(oldUpdater, updater);
    }

    modifier onlyUpdater() {
        require(msg.sender == updater, "You're not the updater");
        _;
    }

    event UpdaterChanged(address indexed oldUpdater, address indexed newUpdater);
}

contracts/Oracle/SovrynSwapPriceFeed.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./IPriceFeed.sol";
import "./ICryptoPairOracle.sol";
import "../Utils/Sovryn/ISovrynLiquidityPoolV1Converter.sol";
import "../Utils/Sovryn/ISovrynAnchor.sol";
import "../BSM/Investors/AMM/interfaces/ISovrynSwapNetwork.sol";

// We need feeds with fiats prices. For now on RSK chain there are no such feeds.
// We populate our own feeds
contract SovrynSwapPriceFeed is IPriceFeed, ICryptoPairOracle, Ownable {
    uint256 private constant PRECISION = 1e12;

    ISovrynSwapNetwork public sovrynNetwork;
    address public tokenSource;
    address public tokenTarget;
    uint256 public priceDisparityTolerance_d12;
    address public updater;
    uint256 public timeBeforeShouldUpdate;
    uint256 public timeBeforeMustUpdate;
    uint256 public updateTimestamp;
    uint256 public oraclePrice;

    constructor(
        address _sovrynNetworkAddress,
        address _tokenSource,
        address _tokenTarget,
        uint256 _priceDisparityTolerance_d12,
        address _updater,
        uint256 _timeBeforeShouldUpdate,
        uint256 _timeBeforeMustUpdate
    ) {
        require(_sovrynNetworkAddress != address(0), "SovrynNetwork address cannot be 0");
        require(_tokenSource != address(0), "TokenSource address cannot be 0");
        require(_tokenTarget != address(0), "TokenTarget address cannot be 0");
        require(_updater != address(0), "Updater address cannot be 0");
        require(_timeBeforeMustUpdate >= 60, "TimeBeforeMustUpdate must be at least 60 seconds");
        require(_timeBeforeShouldUpdate <= _timeBeforeMustUpdate, "TimeBeforeShouldUpdate must be <= timeBeforeMustUpdate");

        sovrynNetwork = ISovrynSwapNetwork(_sovrynNetworkAddress);
        tokenSource = _tokenSource;
        tokenTarget = _tokenTarget;
        priceDisparityTolerance_d12 = _priceDisparityTolerance_d12;
        updater = _updater;
        timeBeforeShouldUpdate = _timeBeforeShouldUpdate;
        timeBeforeMustUpdate = _timeBeforeMustUpdate;
    }

    // IPriceFeed

    function decimals() external pure override(IPriceFeed, ICryptoPairOracle) returns (uint8) {
        return 12;
    }

    function price() external view override returns (uint256) {
        require(oraclePrice != 0, "Oracle not yet initiated");
        require(block.timestamp < (updateTimestamp + timeBeforeMustUpdate), "Price is stale. Update oracle");
        return oraclePrice;
    }

    // ICryptoPairOracle

    function consult(address tokenIn, uint256 amountIn) external view override returns (uint256) {
        require(tokenIn == tokenSource, "This oracle only accepts consulting source token input");
        require(oraclePrice != 0, "Oracle not yet initiated");
        require(block.timestamp < (updateTimestamp + timeBeforeMustUpdate), "Price is stale. Update oracle");
        return (oraclePrice * amountIn) / PRECISION;
    }

    function updateOracle() external pure override {
        revert("use updateOracleWithVerification() instead");
    }

    function shouldUpdateOracle() external pure override returns (bool) {
        return false;
    }

    // Own methods

    function shouldUpdateOracleWithVerification() external view returns (bool) {
        return block.timestamp > (updateTimestamp + timeBeforeShouldUpdate);
    }

    function updateOracleWithVerification(uint256 verificationPrice_d12) external onlyUpdater {
        IERC20[] memory conversionPath = sovrynNetwork.conversionPath(IERC20(tokenSource), IERC20(tokenTarget));

        require(conversionPath.length == 3, "conversion path must be direct");
        ISovrynAnchor anchor = ISovrynAnchor(address(conversionPath[1]));
        ISovrynLiquidityPoolV1Converter sovrynConverter = ISovrynLiquidityPoolV1Converter(anchor.owner());

        (uint256 amountMinusFee, uint256 fee) = sovrynConverter.targetAmountAndFee(tokenSource, tokenTarget, PRECISION);
        uint256 newPrice = amountMinusFee + fee;
        uint256 priceDifference = verificationPrice_d12 > newPrice ? verificationPrice_d12 - newPrice : newPrice - verificationPrice_d12;
        require((priceDifference * PRECISION) / newPrice < priceDisparityTolerance_d12, "Price disparity too big");
        oraclePrice = newPrice;
        updateTimestamp = block.timestamp;
        emit PriceChanged(oraclePrice);
    }

    // Setters

    function setTimeBeforeShouldUpdate(uint256 _timeBeforeShouldUpdate) public onlyOwner {
        require(_timeBeforeShouldUpdate <= timeBeforeMustUpdate, "TimeBeforeShouldUpdate must be <= timeBeforeMustUpdate");
        timeBeforeShouldUpdate = _timeBeforeShouldUpdate;
    }

    function setTimeBeforeMustUpdate(uint256 _timeBeforeMustUpdate) public onlyOwner {
        require(_timeBeforeMustUpdate >= 60, "TimeBeforeMustUpdate must be at least 60 seconds");
        timeBeforeMustUpdate = _timeBeforeMustUpdate;
    }

    function setPriceDisparityTolerance_d12(uint256 _priceDisparityTolerance_d12) external onlyOwner {
        priceDisparityTolerance_d12 = _priceDisparityTolerance_d12;
    }

    function setUpdater(address newUpdater) external onlyOwner {
        require(newUpdater != address(0), "Updater cannot be set to the zero address");

        address oldUpdater = updater;
        updater = newUpdater;
        emit UpdaterChanged(oldUpdater, updater);
    }

    modifier onlyUpdater() {
        require(msg.sender == updater, "You're not updater");
        _;
    }

    event UpdaterChanged(address indexed oldUpdater, address indexed newUpdater);
    event PriceChanged(uint256 indexed newPrice);
}

contracts/Oracle/FiatToFiatPseudoOracleF.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./IPriceFeed.sol";

// We need feeds with fiats prices. For now on RSK chain there are no such feeds.
// We populate our own feeds
contract FiatToFiatPseudoOracleFeed is IPriceFeed, Ownable {
    uint8 private constant DECIMALS = 12;
    uint256 private constant PRICE_PRECISION = 1e12;
    uint256 private constant SECONDS_IN_DAY = 60 * 60 * 24;

    uint256 private recentPrice;
    uint256 public lastUpdateTimestamp;
    uint256 public maxDayChange_d12 = 1e11; // 10%

    address public updater;

    constructor(address _updater, uint256 _recentPrice) {
        require(_updater != address(0), "Updater address cannot be 0");

        updater = _updater;
        recentPrice = _recentPrice;
        lastUpdateTimestamp = block.timestamp;
    }

    function decimals() external pure override returns (uint8) {
        return DECIMALS;
    }

    function price() external view override returns (uint256) {
        return recentPrice;
    }

    function setUpdater(address newUpdater) external onlyOwner {
        require(newUpdater != address(0), "Updater cannot be set to the zero address");

        address oldUpdater = updater;
        updater = newUpdater;
        emit UpdaterChanged(oldUpdater, updater);
    }

    function setPrice(uint256 _price) external onlyUpdaterOrOwner {
        if (_msgSender() != owner()) {
            uint256 diff = _price > recentPrice ? _price - recentPrice : recentPrice - _price;

            uint256 dayChange_d12 = (PRICE_PRECISION * diff * SECONDS_IN_DAY) / recentPrice / (block.timestamp - lastUpdateTimestamp);

            require(dayChange_d12 <= maxDayChange_d12, "Price change too big");
        }

        recentPrice = _price;
        lastUpdateTimestamp = block.timestamp;
        emit PriceChanged(_price);
    }

    function setMaxDayChange_d12(uint256 _maxDayChange_d12) external onlyOwner {
        maxDayChange_d12 = _maxDayChange_d12;
        emit MaxDayChangeChanged(_maxDayChange_d12);
    }

    modifier onlyUpdaterOrOwner() {
        require(_msgSender() == updater || _msgSender() == owner(), "You're not updater");
        _;
    }

    event UpdaterChanged(address indexed oldUpdater, address indexed newUpdater);
    event PriceChanged(uint256 indexed newPrice);
    event MaxDayChangeChanged(uint256 indexed newMaxDayChange_d12);
}

contracts/Oracle/UniswapOracle/UniswapPa.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "../ICryptoPairOracle.sol";
import "./libs/FixedPoint.sol";
import "./libs/UQ112x112.sol";

// Fixed window oracle that recomputes the average price for the entire period once every period
// Note that the price average is only guaranteed to be over at least 1 period, but may be over a longer period
contract UniswapPairOracle is Ownable, ICryptoPairOracle {
    using FixedPoint for *;

    uint8 private constant DECIMALS = 18;

    uint256 public period = 3600; // 1 hour TWAP (time-weighted average price)
    uint256 private constant MAXIMUM_PERIOD = 3600 * 48; // 48 hours
    uint256 private constant MINIMUM_PERIOD = 60 * 5; // 5 minutes

    uint256 public consult_leniency = 60 * 15; // Used for being able to consult past the period end
    bool public allow_stale_consults = false; // If false, consult() will fail if the TWAP is stale

    IUniswapV2Pair public immutable pair;
    address public immutable token0;
    address public immutable token1;

    uint256 public price0CumulativeLast;
    uint256 public price1CumulativeLast;
    uint32 public blockTimestampLast;
    FixedPoint.uq112x112 public price0Average;
    FixedPoint.uq112x112 public price1Average;

    constructor(
        address factoryAddress,
        address tokenA,
        address tokenB
    ) {
        require(factoryAddress != address(0), "Factory address cannot be 0");
        require(tokenA != address(0), "TokenA address cannot be 0");
        require(tokenB != address(0), "TokenB address cannot be 0");

        IUniswapV2Factory factory = IUniswapV2Factory(factoryAddress);

        IUniswapV2Pair _pair = IUniswapV2Pair(factory.getPair(tokenA, tokenB));
        pair = _pair;
        token0 = _pair.token0();
        token1 = _pair.token1();
    }

    function setPeriod(uint256 _period) external onlyOwner {
        require(_period <= MAXIMUM_PERIOD, "PairOracle::setPeriod: > MAXIMUM_PERIOD");
        require(_period >= MINIMUM_PERIOD, "PairOracle::setPeriod: < MINIMUM_PERIOD");
        period = _period;

        emit PeriodSet(_period);
    }

    function setConsultLeniency(uint256 _consult_leniency) external onlyOwner {
        consult_leniency = _consult_leniency;

        emit ConsultLeniencySet(_consult_leniency);
    }

    function setAllowStaleConsults(bool _allow_stale_consults) external onlyOwner {
        allow_stale_consults = _allow_stale_consults;

        emit AllowStaleConsultsSet(_allow_stale_consults);
    }

    function reset() external onlyOwner {
        price0CumulativeLast = pair.price0CumulativeLast(); // Fetch the current accumulated price value (1 / 0)
        price1CumulativeLast = pair.price1CumulativeLast(); // Fetch the current accumulated price value (0 / 1)
        uint112 reserve0;
        uint112 reserve1;

        (reserve0, reserve1, blockTimestampLast) = pair.getReserves();
        require(reserve0 != 0 && reserve1 != 0, "UniswapPairOracle: NO_RESERVES"); // Ensure that there's liquidity in the pair
    }

    // Check if updateOracle() can be called instead of wasting gas calling it
    function shouldUpdateOracle() public view override returns (bool) {
        uint32 blockTimestamp = currentBlockTimestamp();

        unchecked {
            uint32 timeElapsed = blockTimestamp - blockTimestampLast; // Overflow is desired
            return (timeElapsed >= period);
        }
    }

    function updateOracle() external override {
        require(blockTimestampLast > 0, "Oracle not ready");

        (uint256 price0Cumulative, uint256 price1Cumulative, uint32 blockTimestamp) = currentCumulativePrices(address(pair));

        unchecked {
            uint32 timeElapsed = blockTimestamp - blockTimestampLast; // Overflow is desired

            // Ensure that at least one full period has passed since the last update
            require(timeElapsed >= period || owner() == _msgSender(), "UniswapPairOracle: PERIOD_NOT_ELAPSED");

            // Overflow is desired, casting never truncates
            // Cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
            price0Average = FixedPoint.uq112x112(uint224((price0Cumulative - price0CumulativeLast) / timeElapsed));
            price1Average = FixedPoint.uq112x112(uint224((price1Cumulative - price1CumulativeLast) / timeElapsed));

